import React, { Component, useState } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../actions/authActions";
import { Container, Row, Col, Carousel, Button, Form } from "react-bootstrap";
import Fade from "react-reveal";
import image1 from "../image1.jpeg";
import Footer from "../components/Footer";

import Navigation from "../components/Navigation";
import axios from "axios";
var token;

try {
  token = localStorage.getItem("jwtToken").split(" ")[1];
} catch (err) {}

// const [title, setTitle] = useState("");
// const [body, setBody] = useState("");

const Dashboard = (props) => {
  const { user } = props.auth;
  const [title, setTitle] = useState();
  const [body, setBody] = useState();

  const titleChange = (e) => {
    setTitle(e.target.value);
  };
  const bodyChange = (e) => {
    setBody(e.target.value);
  };

  Dashboard.propTypes = {
    logoutUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
  };

  const onLogoutClick = (e) => {
    e.preventDefault();
    props.logoutUser();
  };
  const handleSubmit = (e) => {
    console.log(token);
    e.preventDefault();
    axios
      .post(
        "/api/posts/create",
        {
          title: title,
          body: body,
        },
        {
          headers: {
            "x-auth-token": `Bearer ${token}`,
          },
        }
      )
      .then((res) => {
        console.log(res);
      });
  };
  return (
    <>
      {/* General notes about this page: I don't really like the layout of the first section. It could
    definitely be improved. The second section with the "beliefs" I think makes a lot of sense to add.
    I got those words from the consitution of the club. I like the icons too.  */}
      <div className="dashboard-nav">
        <Navigation></Navigation>
        <div className="header-text">
          <Container>
            <Row>
              <p className="heading-about">WELCOME, {user.name}</p>
            </Row>
          </Container>
        </div>
      </div>
      <br />
      <div>
        <Container className="fencing-body">
          <Form noValidate onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="email"
                placeholder="username"
                onChange={titleChange}
                // value={this.state.title}
                // error={errors.email}
                // id="email"
                // type="email"
                // className={classnames("", {
                //   invalid: errors.email || errors.emailnotfound,
                // })}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Body</Form.Label>
              <Form.Control
                placeholder="body"
                onChange={bodyChange}
                //value={this.state.body}
                // error={errors.password}
                // id="password"
                // type="password"
                // className={classnames("", {
                //   invalid: errors.password || errors.passwordincorrect,
                // })}
              />
            </Form.Group>
            <Button variant="dark" type="submit">
              Submit
            </Button>
          </Form>
          <Button variant="dark" type="submit" onClick={onLogoutClick}>
            Logout
          </Button>
        </Container>
      </div>
      <Footer></Footer>
    </>
  );
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});
export default connect(mapStateToProps, { logoutUser })(Dashboard);
