import React, { useState } from "react";
import { Nav, Container, Button, Row, Col, Form } from "react-bootstrap";
import UserContext from "../../context/userContext";

import axios from "axios";
import { LinkContainer } from "react-router-bootstrap";
import Fade from "react-reveal";
import Faq from "react-faq-component";
import image1 from "../image1.jpeg";
import divider from "../divider1.png";
import Footer from "../components/Footer";

import Navigation from "../components/Navigation";
import "../styles/index.css";
import { Component } from "react";
import context from "react-bootstrap/esm/AccordionContext";

export default function Admin() {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [error, setError] = useState();
  const { setUserData } = useContext(UserContext);
  const history = useHistory();
  const submit = async (e) => {
    e.preventDefault();
    try {
      const loginUser = { email, password };
      const loginResponse = await axios.post(
        "http://localhost:5000/users/login",
        loginUser
      );
      setUserData({
        token: loginResponse.data.token,
        user: loginResponse.data.user,
      });
      localStorage.setItem("auth-token", loginResponse.data.token);
      history.push("/");
    } catch (err) {
      err.response.data.msg && setError(err.response.data.msg);
    }
  };
  return (
    <>
      <script
        src="https://kit.fontawesome.com/a17da011a4.js"
        crossorigin="anonymous"
      ></script>
      <div className="faq-nav">
        <Navigation></Navigation>
        <div className="header-text">
          <Container>
            <Row>
              <p className="heading-about">SPEAK FRIEND AND ENTER</p>
            </Row>
          </Container>
        </div>
      </div>
      <br />
      <div>
        <Container className="fencing-body">
          <Form onSubmit={submit} method="post">
            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="text"
                placeholder="Email"
                name="email"
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Password"
                name="password"
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Log-in
            </Button>
          </Form>
        </Container>
        <br />
        <br />
      </div>
      <Footer></Footer>
    </>
  );
}
