import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import Header from "../components/Header";
import "../styles/index.css";

import image1 from "../image1.jpeg";
import divider from "../divider1.png";

function FadeInSection(props) {
  const [isVisible, setVisible] = React.useState(true);
  const domRef = React.useRef();
  React.useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => setVisible(entry.isIntersecting));
    });
    observer.observe(domRef.current);
    return () => observer.unobserve(domRef.current);
  }, []);
  return (
    <div
      className={`fade-in-section ${isVisible ? "is-visible" : ""}`}
      ref={domRef}
    >
      {props.children}
    </div>
  );
}

const Index = () => (
  <>
    <Header></Header>
    <br />
    <div>
      <Container className="home-intro">
        <Row>
          <Col>
            <img src={image1} width="500px" />
          </Col>
          <Col>
            <h1>The Official Fencing Club of Purdue University</h1>
            <br />
            <br />
            <p className="home-text">
              I'm baby cold-pressed pour-over tofu bicycle rights. Tofu meh
              chambray drinking vinegar, lo-fi man bun lyft. Portland fanny pack
              8-bit, fixie you probably haven't heard of them shoreditch
              artisan. Farm-to-table letterpress cloud bread art party stumptown
              vaporware health goth waistcoat kombucha prism ethical shabby
              chic.
            </p>
          </Col>
        </Row>
        <br />
        <br />
        <br />
        <img src={divider} height="80px" width="1000px" />
        <br />
        <br />
        <br />
        <br />
        <Row>
          <Col>
            <br />
            <br />
            <br />
            <p className="home-text">
              Nearly one hundred years ago, we humbly began as a 5-member men's
              varsity foil squad in 1922. As the varsity squad gained
              popularity, in 1935, an honorary fraternal order, Silver Mask,
              formed. In 1970, men's varsity fencing would dissolve into Purdue
              Fencing Club in its modern form. Since then, Purdue Fencing Club
              continues to grow and we now have over 50 members!
            </p>
          </Col>
          <Col>
            <img src={image1} width="500px" />
          </Col>
        </Row>
      </Container>
    </div>
  </>
);

export default Index;
