import React, { useContext } from "react";
import { Nav, Container, Button, Row, Col, Form } from "react-bootstrap";
import axios from "axios";
import { LinkContainer } from "react-router-bootstrap";
import Fade from "react-reveal";
import Faq from "react-faq-component";
import image1 from "../image1.jpeg";
import divider from "../divider1.png";
import Footer from "../components/Footer";

import Navigation from "../components/Navigation";
import "../styles/index.css";
import { Component } from "react";
import context from "react-bootstrap/esm/AccordionContext";

export default function Admin() {
  const userRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = (e) => {
    e.preventDefault();
    try {
      const res = axios.post("/api/auth/login", {
        username: userRef.current.value,
        password: passwordRef.current.value,
      });
    } catch (err) {}
  };
  return (
    <>
      <script
        src="https://kit.fontawesome.com/a17da011a4.js"
        crossorigin="anonymous"
      ></script>
      <div className="faq-nav">
        <Navigation></Navigation>
        <div className="header-text">
          <Container>
            <Row>
              <p className="heading-about">SPEAK FRIEND AND ENTER</p>
            </Row>
          </Container>
        </div>
      </div>
      <br />
      <div>
        <Container className="fencing-body">
          <Form onSubmit={handleSubmit} method="post">
            <Form.Group className="mb-3">
              <Form.Label>Username</Form.Label>
              <Form.Control
                type="text"
                placeholder="Username"
                name="username"
                ref={userRef}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Password"
                name="password"
                ref={passwordRef}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Log-in
            </Button>
          </Form>
        </Container>
        <br />
        <br />
      </div>
      <Footer></Footer>
    </>
  );
}
