import React, { Component, useState } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../actions/authActions";
import { Container, Row, Col, Carousel, Button, Form } from "react-bootstrap";
import Fade from "react-reveal";
import image1 from "../image1.jpeg";
import Footer from "../components/Footer";

import Navigation from "../components/Navigation";
import axios from "axios";
let token;

// const [title, setTitle] = useState("");
// const [body, setBody] = useState("");

const Dashboard = (props) => {
  try {
    token = localStorage.getItem("jwtToken").split(" ")[1];
  } catch (err) {
    console.log("Token doesn't exist!");
  }

  const { user } = props.auth;
  const [title, setTitle] = useState();
  const [body, setBody] = useState();

  const titleChange = (e) => {
    setTitle(e.target.value);
  };
  const bodyChange = (e) => {
    setBody(e.target.value);
  };

  Dashboard.propTypes = {
    logoutUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
  };

  const onLogoutClick = (e) => {
    e.preventDefault();
    props.logoutUser();
  };
  const handleSubmit = (e) => {
    console.log(token);
    e.preventDefault();
    axios
      .post(
        "/api/posts/create",
        {
          title: title,
          body: body,
        },
        {
          headers: {
            "x-auth-token": `${token}`,
          },
        }
      )
      .then((res) => {
        console.log(res);
      });
  };
  return (
    <>
      <div className="dashboard-nav">
        <Navigation></Navigation>
        <div className="header-text">
          <Container>
            <Row>
              <p className="heading-about">WELCOME, {user.name}</p>
            </Row>
          </Container>
        </div>
      </div>
      <br />
      <div>
        <Container className="fencing-body">
          <Container className="fencing-submit">
            <h1 className="fencing-header">Submit a new post</h1>
            <Row>
              <Col>
                <Form
                  noValidate
                  onSubmit={handleSubmit}
                  className="submit-form"
                >
                  <Form.Group className="mb-3">
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="username"
                      onChange={titleChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Body</Form.Label>
                    <Form.Control placeholder="body" onChange={bodyChange} />
                  </Form.Group>
                  <Button variant="dark" type="submit">
                    Submit
                  </Button>
                </Form>
              </Col>
            </Row>
          </Container>
          <br />
          <br />
          <hr></hr>
          <Container className="fencing-submit">
            <h1 className="fencing-header">Edit a post</h1>
            <Row>
              <Col>
                <Form
                  noValidate
                  onSubmit={handleSubmit}
                  className="submit-form"
                >
                  <Form.Group className="mb-3">
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="username"
                      onChange={titleChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Body</Form.Label>
                    <Form.Control placeholder="body" onChange={bodyChange} />
                  </Form.Group>
                  <Button variant="dark" type="submit">
                    Submit
                  </Button>
                </Form>
              </Col>
            </Row>
          </Container>
          <br />
          <br />
          <hr></hr>
          <Container className="fencing-submit">
            <h1 className="fencing-header">Delete a post</h1>
            <Row>
              <Col>
                <Form
                  noValidate
                  onSubmit={handleSubmit}
                  className="submit-form"
                >
                  <Form.Group className="mb-3">
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="username"
                      onChange={titleChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>Body</Form.Label>
                    <Form.Control placeholder="body" onChange={bodyChange} />
                  </Form.Group>
                  <Button variant="dark" type="submit">
                    Submit
                  </Button>
                </Form>
              </Col>
            </Row>
          </Container>
          <Button variant="dark" type="submit" onClick={onLogoutClick}>
            Logout
          </Button>
        </Container>
      </div>
      <Footer></Footer>
    </>
  );
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});
export default connect(mapStateToProps, { logoutUser })(Dashboard);
