import React, { useState, useContext } from "react";
import { useHistory, Link, withRouter } from "react-router-dom";
import classnames from "classnames";
import PropTypes from "prop-types";
import { loginUser, registerUser } from "../actions/authActions";
import { connect } from "react-redux";

import { Nav, Container, Button, Row, Col, Form } from "react-bootstrap";
import UserContext from "../context/userContext";

import axios from "axios";
import Footer from "../components/Footer";

import Navigation from "../components/Navigation";
import "../styles/index.css";
import { Component } from "react";
import context from "react-bootstrap/esm/AccordionContext";

import Login from "../components/Login";
import { compose } from "redux";

class Admin extends Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
      errors: {},
    };
  }

  componentDidMount() {
    // If logged in and user navigates to Login page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
      this.props.router.push("/dashboard"); // push user to dashboard when they login
    }
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.auth.isAuthenticated) {
      this.props.router.push("/dashboard"); // push user to dashboard when they login
    }
    if (nextProps.errors) {
      this.setState({
        errors: nextProps.errors,
      });
    }
  }

  onChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };
  onSubmit = (e) => {
    e.preventDefault();
    const userData = {
      email: this.state.email,
      password: this.state.password,
    };
    this.props.loginUser(userData);
  };
  render() {
    const { errors } = this.state;
    return (
      <>
        <script
          src="https://kit.fontawesome.com/a17da011a4.js"
          crossorigin="anonymous"
        ></script>
        <div className="faq-nav">
          <Navigation></Navigation>
          <div className="header-text">
            <Container>
              <Row>
                <p className="heading-about">SPEAK FRIEND AND ENTER</p>
              </Row>
            </Container>
          </div>
        </div>
        <br />
        <div>
          <Container className="fencing-body">
            <div className="container">
              <div style={{ marginTop: "4rem" }} className="row">
                <div className="col s8 offset-s2">
                  <Link to="/" className="btn-flat waves-effect">
                    <i className="material-icons left">keyboard_backspace</i>{" "}
                    Back to home
                  </Link>
                  <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                    <h4>
                      <b>Login</b> below
                    </h4>
                    <p className="grey-text text-darken-1">
                      Don't have an account?{" "}
                      <Link to="/register">Register</Link>
                    </p>
                  </div>
                  <form noValidate onSubmit={this.onSubmit}>
                    <div className="input-field col s12">
                      <input
                        onChange={this.onChange}
                        value={this.state.email}
                        error={errors.email}
                        id="email"
                        type="email"
                        className={classnames("", {
                          invalid: errors.email || errors.emailnotfound,
                        })}
                      />
                      <label htmlFor="email">Email</label>
                      <span className="red-text">
                        {errors.email}
                        {errors.emailnotfound}
                      </span>
                      />
                      <label htmlFor="email">Email</label>
                    </div>
                    <div className="input-field col s12">
                      <input
                        onChange={this.onChange}
                        value={this.state.password}
                        error={errors.password}
                        id="password"
                        type="password"
                        className={classnames("", {
                          invalid: errors.password || errors.passwordincorrect,
                        })}
                      />
                      <label htmlFor="password">Password</label>
                      <span className="red-text">
                        {errors.password}
                        {errors.passwordincorrect}
                      </span>
                      / ><label htmlFor="password">Password</label>
                    </div>
                    <div
                      className="col s12"
                      style={{ paddingLeft: "11.250px" }}
                    >
                      <button
                        style={{
                          width: "150px",
                          borderRadius: "3px",
                          letterSpacing: "1.5px",
                          marginTop: "1rem",
                        }}
                        type="submit"
                        className="btn btn-large waves-effect waves-light hoverable blue accent-3"
                      >
                        Login
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </Container>
          <br />
          <br />
        </div>
        <Footer></Footer>
      </>
    );
  }
}

Login.propTypes = {
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  auth: state.auth,
  errors: state.errors,
});

export default compose(connect(mapStateToProps, loginUser)(withRouter(Admin)));
