import React, { useState, useEffect } from "react";
import { withRouter, useParams } from "react-router-dom";
import {
  Nav,
  Container,
  Button,
  Row,
  Col,
  Card,
  CardGroup,
} from "react-bootstrap";
import axios from "axios";
import { LinkContainer } from "react-router-bootstrap";
import Fade from "react-reveal";
import image1 from "../image1.jpeg";
import divider from "../divider1.png";

import Footer from "../components/Footer";
import Navigation from "../components/Navigation";
import "../styles/index.css";

const Post = (props) => {
  const post = useParams();

  const [thisPost, setThisPost] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      const res = await axios.get(`/api/blogs/display/${post.id}`);
      thisPost = res;
      setThisPost(res.data);
      console.log(res.data);
    };

    fetchPosts();
  });

  return (
    <>
      <script
        src="https://kit.fontawesome.com/a17da011a4.js"
        crossorigin="anonymous"
      ></script>
      <div className="join-nav">
        <Navigation></Navigation>
        <div>
          <Container className="join-header">
            <Row>
              <p className="heading-about">EN GARDE.</p>
              <p className="heading-about">
                READY. <span className="join-fence-text">FENCE.</span>
              </p>
            </Row>
          </Container>
        </div>
      </div>
      <br />
      <br />
      <br />
      <Container className="join-content-container">{thisPost.body}</Container>
      <br />
      <Footer></Footer>
    </>
  );
};

export default withRouter(Post);
